import { makeStyles, Paper, Fade } from "@material-ui/core";
import VizSensor from "react-visibility-sensor";

import React, { useState } from "react";

const SKILLS = [
	{
		name: "React",
	},
	{
		name: "Node JS",
	},
	{
		name: "Express",
	},
	{
		name: "Redux",
	},
	{
		name: "Python",
	},
	{
		name: "Scikit Learn",
	},
	{
		name: "CNN",
	},
	{
		name: "ANN",
	},
	{
		name: "JavaScript",
	},
	{
		name: "C#",
	},
	{
		name: "DEEP LEARNING",
	},
	{
		name: "TensorFlow",
	},
	{
		name: "MongoDB",
	},
	{
		name: "NEXT JS",
	},
	{
		name: "MACHINE LEARNING",
	},
];

const useStyles = makeStyles((theme) => ({
	root: {
		backdropFilter: "blur(5px)",
		backgroundColor: "#DDDDDD",
		color: "#ced4da",
		position: "relative",
		height: "50vh",
		background: `url('../coffee-apple.jpg')`,
	},
	title: {
		width: "100vw",
		textAlign: "center",
		zIndex: 1,
		fontSize: "36px",
		color: "#000814",
	},
	intro: {
		position: "absolute",
		top: "50%",
		left: "25%",
		transform: "translate(-50%,-50%)",
		width: "100vw",
		textAlign: "center",
		zIndex: 1,
	},
	txtContainer: {
		position: "absolute",
		top: "50%",
		left: "70%",
		transform: "translate(-50%,-50%)",
		textAlign: "center",
		zIndex: 1,
	},
	txt: {
		fontSize: "14px",
	},
	avatar: {
		width: theme.spacing(30),
		height: theme.spacing(30),
		margin: theme.spacing(1),
	},
	skills: {
		display: "flex",
		flexWrap: "wrap",
		justifyContent: "center",
		alignItems: "center",
	},
	skill: {
		padding: "1rem",
		margin: "1rem",
		backgroundColor: "#000814",
		// borderRadius: "100%",
		fontSize: "24px",
		[theme.breakpoints.down("md")]: {
			padding: "0.5rem",
			margin: "0.5rem",
		},
	},
}));
const Skills = () => {
	const classes = useStyles();
	const [show, setShow] = useState(false);

	return (
		<React.Fragment>
			<VizSensor
				onChange={(isVisible) => {
					setShow(isVisible);
				}}
			>
				<Paper className={classes.root}>
					<div className={classes.title}>SKILLS</div>
					<Fade in={show} timeout={1000}>
						<div className={classes.skills}>
							{SKILLS.map((skill, index) => {
								return (
									<div className={classes.skill}>
										{skill.name}
									</div>
								);
							})}
						</div>
					</Fade>
				</Paper>
			</VizSensor>
		</React.Fragment>
	);
};

export default Skills;
