import { makeStyles, Paper, Fade } from "@material-ui/core";
import VizSensor from "react-visibility-sensor";

import React, { useState } from "react";

const useStyles = makeStyles((theme) => ({
	root: {
		backdropFilter: "blur(5px)",
		backgroundColor: "transparent",
		color: "#ced4da",
		position: "relative",
	},
	title: {
		width: "100vw",
		textAlign: "center",
		zIndex: 1,
		fontSize: "36px",
		color: "#000814",
	},
	intro: {
		position: "absolute",
		top: "50%",
		left: "25%",
		transform: "translate(-50%,-50%)",
		width: "100vw",
		textAlign: "center",
		zIndex: 1,
	},
	txtContainer: {
		position: "absolute",
		top: "50%",
		left: "70%",
		transform: "translate(-50%,-50%)",
		textAlign: "center",
		zIndex: 1,
	},
	txt: {
		fontSize: "14px",
	},
	avatar: {
		width: theme.spacing(30),
		height: theme.spacing(30),
		margin: theme.spacing(1),
	},
	skills: {
		display: "flex",
		flexWrap: "wrap",
		justifyContent: "center",
		alignItems: "center",
	},
	skill: {
		padding: "1rem",
		margin: "1rem",
		backgroundColor: "#000814",
		// borderRadius: "100%",
		fontSize: "24px",
		[theme.breakpoints.down("md")]: {
			padding: "0.5rem",
			margin: "0.5rem",
			fontSize: "12px",
		},
	},
}));
const Education = () => {
	const classes = useStyles();
	const [show, setShow] = useState(false);

	return (
		<React.Fragment>
			<VizSensor
				onChange={(isVisible) => {
					setShow(isVisible);
				}}
			>
				<Paper className={classes.root}>
					<div className={classes.title}>EDUCATION</div>
					<Fade in={show} timeout={1000}>
						<div className={classes.skills}>
							مهندسی کامپیوتر of Bachelor دانشگاه تبریز
							:University/Institute From 1397 GPA : 18
						</div>
					</Fade>
				</Paper>
			</VizSensor>
		</React.Fragment>
	);
};

export default Education;
