import { makeStyles, Avatar, Grid, Fade } from "@material-ui/core";
import VizSensor from "react-visibility-sensor";

import React, { useState } from "react";

const SKILLS = [
	{
		name: "react",
	},
	{
		name: "react",
	},
	{
		name: "react",
	},
	{
		name: "react",
	},
	{
		name: "react",
	},
	{
		name: "react",
	},
	{
		name: "react",
	},
	{
		name: "react",
	},

	{
		name: "react",
	},
];

const useStyles = makeStyles((theme) => ({
	root: {
		backgroundColor: "#ced4da",
		color: "#adb5bd",
		position: "relative",
		height: "100vh",
	},
	title: {
		width: "100vw",
		textAlign: "center",
		zIndex: 1,
		fontSize: "36px",
		color: "#6c757d",
		borderBottom: "3px solid #6c757d",
		background: "url('../../p6.jpg')",
	},
	intro: {
		position: "absolute",
		top: "50%",
		left: "25%",
		transform: "translate(-50%,-50%)",
		width: "100vw",
		textAlign: "center",
		zIndex: 1,
	},
	txtContainer: {
		position: "absolute",
		top: "50%",
		left: "70%",
		transform: "translate(-50%,-50%)",
		textAlign: "center",
		zIndex: 1,
	},
	txt: {
		fontSize: "14px",
	},
	avatar: {
		width: theme.spacing(30),
		height: theme.spacing(30),
		margin: theme.spacing(1),
	},
	skills: {
		display: "flex",
		flexWrap: "wrap",
		justifyContent: "center",
		alignItems: "center",
	},
	skill: {
		padding: "2rem",
		margin: "2rem",
		backgroundColor: "#6c757d",
		borderRadius: "50%",
		fontSize: "24px",
	},
}));
const Skills = () => {
	const classes = useStyles();
	const [show, setShow] = useState(false);

	return (
		<React.Fragment>
			<VizSensor
				onChange={(isVisible) => {
					setShow(isVisible);
				}}
			>
				<div className={classes.root}>
					<div className={classes.title}>SKILLS</div>
					<div className={classes.skills}>
						{SKILLS.map((skill, index) => {
							return (
								<div className={classes.skill}>
									{skill.name}
								</div>
							);
						})}
					</div>
				</div>
			</VizSensor>
		</React.Fragment>
	);
};

export default Skills;
